import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';

class App extends Component {

  state = {
     titleBtn : 'Start',
  };

  StartTimer = () => {
    if (this.state.titleBtn === 'Start') {
      this.setState({
        titleBtn : 'Start',
      })
    }

    if (this.state.titleBtn === 'Stop') {
      this.setState({
        titleBtn : 'Start',
      })
    }
  }

  render(){
    return(
      <div className="App">
      <header className="App-header">
       <div className="jumbotron">
         <button className="btn btn-primary" onClick={this.StartTimer}>{this.state.titleBtn}</button>
       </div>
      </header>
    </div>
    );
  }
}

export default App;
