import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './index.css';

class App extends Component {

  state = {
     titleBtn : 'Start',

  };

  StartTimer = () => {
    if (this.state.titleBtn === 'Start') {
      this.setState({
        titleBtn : 'Stop',
      })
    }

    if (this.state.titleBtn === 'Stop') {
      this.setState({
        titleBtn : 'Start',
      })
    }
  }

  ResetTimer = () =>{
    console.log('Reset')
  }

  render(){
    return(
      <div className="App">
        <div className="jumbotron text-center">
         <div className="form">
            <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Nam facere molestias corrupti quo, earum ut officia architecto harum aperiam voluptatibus explicabo, nihil non perspiciatis ab tempora aliquid hic? Laudantium, animi.</p>
            <button className="btn btn-primary my-2" onClick={this.StartTimer}>{this.state.titleBtn}</button>
            <button className="btn btn-danger my-2" onClick={this.ResetTimer}>Reset</button>
          </div>
        </div>
    </div>
    );
  }
}

export default App;
