import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './index.css';

class App extends Component {

  state = {
     titleBtn : 'Start',
     timeDefault : '00:00:00',
     miliSecond : 0,
     Second : 0,
     Minute : 0,
     Hours  : 0,     
  };

  StartTimer = () => {

    // start button
    if (this.state.titleBtn === 'Start') {
      this.setState({
        titleBtn : 'Stop',
      });

      this.miliSecond = setInterval(() => this.setState({
        miliSecond: this.state.miliSecond + 1,
        Second : Math.floor(this.state.miliSecond / 60)
      }),100 )
    }

    // stop button
    if (this.state.titleBtn === 'Stop') {
      this.setState({
        titleBtn : 'Start',
      });
      clearInterval(this.miliSecond)
    }
  }

  ResetTimer = () =>{
    console.log('Reset')
  }

  render(){
    return(
      <div className="App">
         <div className="form-signin">
           <div className="card">
             <div className="card-body">
               <h1>
               {this.state.Hours}:
               {this.state.Minute}:
               {this.state.Second}:
               {this.state.miliSecond}
               </h1>
             </div>
           </div>
           <button className="btn btn-primary my-2" onClick={this.StartTimer}>{this.state.titleBtn}</button>
          <button className="btn btn-danger my-2" onClick={this.ResetTimer}>Reset</button>
          </div>
    </div>
    );
  }
}

export default App;
