import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';

class App extends Component {

  state = {
     titleBtn : 'Start',

  };

  StartTimer = () => {
    if (this.state.titleBtn === 'Start') {
      this.setState({
        titleBtn : 'Stop',
      })
    }

    if (this.state.titleBtn === 'Stop') {
      this.setState({
        titleBtn : 'Start',
      })
    }
  }

  ResetTimer = () =>{
    console.log('Reset')
  }

  render(){
    return(
      <div className="App">

       <div className="jumbotron text-center">
         <div className="container">
           <div className="row">
             <div className="col-md-4 col-offset-8">

           <div className="card">
             <div className="card-body">
              <button className="btn btn-primary my-2" onClick={this.StartTimer}>{this.state.titleBtn}</button>
              <button className="btn btn-danger my-2" onClick={this.ResetTimer}>Reset</button>
             </div>
           </div>
             </div>
           </div>
         </div>
       </div>

    </div>
    );
  }
}

export default App;
