import 'bootstrap/dist/css/bootstrap.css';

class App extends Component {

  State = {
     titleBtn : "Start",
  };

  StartTimer(){
    this.setState = { titleBtn : "Stop"}
    console.log("hallo");
  }

  render(){
    return(
      <div className="App">
      <header className="App-header">
       <div className="jumbotron">
         <button className="btn btn-primary" onClick={this.StartTimer}>{titleBtn}</button>
       </div>
      </header>
    </div>
    );
  }
}

export default App;
