import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './index.css';

class App extends Component {

  state = {
     titleBtn : 'Start',
     timeDefault : '00:00:00',

  };

  StartTimer = () => {
    if (this.state.titleBtn === 'Start') {
      this.setState({
        titleBtn : 'Stop',
      })
    }

    if (this.state.titleBtn === 'Stop') {
      this.setState({
        titleBtn : 'Start',
      })
    }
  }

  ResetTimer = () =>{
    console.log('Reset')
  }

  render(){
    return(
      <div className="App">
         <div className="form-signin">
           <div className="card">
             <div className="card-body">
               <h1>0.0.0</h1>
             </div>
           </div>
           <button className="btn btn-primary my-2" onClick={this.StartTimer}>{this.state.titleBtn}</button>
          <button className="btn btn-danger my-2" onClick={this.ResetTimer}>Reset</button>
          </div>
    </div>
    );
  }
}

export default App;
